def execute_math_calculator(expression: str) -> str:
    try:
        allowed_chars = set('0123456789+-*/(). ')
        if not all(c in allowed_chars for c in expression):
            return "❌ 表达式包含非法字符"
        return str(eval(expression))
    except Exception as e:
        return f"❌ 计算错误: {str(e)}"
